dt-button.example:
$('#myTable').DataTable( {
	buttons: [
		{
			extends: 'text',
			text: 'Action'
			action: function ( e, dt, button, config ) {
				// perform some action on the table
			}
		}
	}
} );

new $.fn.dataTable.Buttons( table, {
	buttons: [
		{
			extends: 'text',
			text: 'Action'
			action: function ( e, dt, button, config ) {
				// perform some action on the table
			}
		}
	}
} );
