dt-example.js:
$(document).ready(function() {
	$('#example').wrap('<div id="hide" style="display:none"/>');

	$('#example').DataTable( {
		dom: 'Bfrtip',
		buttons: [
			'copyFlash',
			'csvFlash',
			'excelFlash',
			'pdfFlash'
		]
	} );

	$('#vis').one( 'click', function () {
		$('#hide').css( 'display', 'block' );
	} );

	$('#resize').on( 'click', function () {
		$.fn.dataTable.tables( { visible: true, api: true } ).buttons.resize();
	} );
} );
