dt-example.js:
$(document).ready(function() {
	var table = $('#example').DataTable( {
		buttons: [
			{ extend: 'csv', key: 'a', text: '<u>A</u> CSV button' },
			'copy',
			{
				extend: 'collection',
				buttons: [ 'pdf', 'copy', 'pdf', 'copy', 'pdf', 'copy', 'pdf' ]
			},
			{
				extend: 'collection',
				collectionLayout: 'fixed three-column',
				buttons: [ 'pdf', 'copy', 'pdf', 'copy', 'pdf', 'copy', 'pdf' ]
			},
			{
				text: 'No extend',
				action: function () {
					console.log( 'No extend' );
				}
			}
		]
	} );

	$( 'div.dataTables_length', table.table().container() ).replaceWith(
		table.buttons().container()
	);
} );
